import urllib
import urllib.request

def movie_api():
  
    url="http://175.125.91.94/oasis/service/rest/meta16/getkobis01"
    # ?numOfRows=10&pageNo=3
    # webbrowser.open(url)
    resultXML = urllib.request.urlopen(url)
    result = resultXML.read()
    xmlsoup = BeautifulSoup(result, 'lxml-xml')
 
    te=xmlsoup.findAll("item")
    sil=pd.DataFrame()
 
    for t in te:
        startdate = t.find("issuedDate").text
        updatedate = t.find("temporal").text
        pop = t.find("subDescription").text
        money = t.find("description").text
        rank = t.find("grade").text
        movie_name = t.find("title").text
       
        temp = pd.DataFrame(([[startdate, updatedate, pop, money, rank, movie_name]]), columns=["개봉일","updatedate","관객수","매출액","순위","제목"])
        sil=pd.concat([sil,temp])
 
    sil = sil.reset_index(drop=True)
 
    return sil

